// Parse input từ Agent
const input = $input.first().json;
let query = '';
let action = input.action || 'search';
let userId = input.userId || '';
let category = input.category || ''; // THÊM DÒNG NÀY

// Lấy query/content từ nhiều nguồn có thể
if (typeof input === 'string') {
  query = input;
} else if (input.query) {
  query = input.query;
} else if (input.content) {
  query = input.content;
} else if (input.chatInput) {
  query = input.chatInput;
} else if (input.input) {
  query = input.input;
} else {
  query = JSON.stringify(input);
}

// Phân loại category tự động - CHỈ KHI CHƯA CÓ CATEGORY
if (!category) {
  const queryLower = query.toLowerCase();

  if (queryLower.includes('giá') || queryLower.includes('chi phí') || queryLower.includes('vnđ')) {
    category = 'pricing';
  } else if (queryLower.includes('vật liệu') || queryLower.includes('gỗ') || queryLower.includes('mdf')) {
    category = 'material';
  } else if (queryLower.includes('quy trình') || queryLower.includes('cách làm')) {
    category = 'process';
  } else if (queryLower.includes('sai') || queryLower.includes('sửa') || queryLower.includes('đúng là')) {
    category = 'correction';
  } else if (queryLower.includes('sản phẩm') || queryLower.includes('tủ') || queryLower.includes('bàn')) {
    category = 'product';
  } else {
    category = 'general';
  }
}

return [{
  json: {
    query: query,
    action: action,
    category: category,  // Giờ có thể từ input hoặc tự detect
    userId: userId,
    timestamp: new Date().toISOString()
  }
}];

