// Format kết quả Save
const input = $('Parse Input').first().json;
const saveResult = $('Save to Supabase').first().json;

let message = '';
if (saveResult && saveResult.id) {
  message = `Đã ghi nhớ thành công: "${input.query.substring(0, 150)}${input.query.length > 150 ? '...' : ''}"`;
} else {
  message = `Đã ghi nhớ: "${input.query.substring(0, 150)}${input.query.length > 150 ? '...' : ''}"`;
}

return [{
  json: {
    success: true,
    action: 'saved',
    message: message,
    category: input.category,
    id: saveResult?.id || 'saved'
  }
}];

// Format kết quả Search với xử lý lỗi an toàn
try {
  // Lấy dữ liệu từ các node trước
  let searchResult = null;
  let input = null;

  try {
    searchResult = $('Search Supabase').first().json;
  } catch (e) {
    // Nếu node Search Supabase lỗi hoặc không có output
    console.error('Lỗi khi lấy kết quả từ Search Supabase:', e.message);
    searchResult = null;
  }

  try {
    input = $('Parse Input').first().json;
  } catch (e) {
    console.error('Lỗi khi lấy input:', e.message);
    input = { query: 'N/A' };
  }

  // Kiểm tra nếu Supabase trả về lỗi
  if (searchResult && searchResult.error) {
    return [{
      json: {
        success: false,
        action: 'search',
        message: `Lỗi khi tìm kiếm: ${searchResult.error.message || searchResult.error}`,
        query: input.query || 'N/A',
        results: [],
        error: searchResult.error
      }
    }];
  }

  // Kiểm tra kết quả rỗng hoặc null
  if (!searchResult) {
    return [{
      json: {
        success: false,
        action: 'search',
        message: `Không nhận được kết quả từ database. Có thể database đang lỗi hoặc không có dữ liệu.`,
        query: input.query || 'N/A',
        results: []
      }
    }];
  }

  // Parse kết quả - xử lý nhiều định dạng có thể
  let results = [];

  try {
    if (Array.isArray(searchResult)) {
      results = searchResult;
    } else if (searchResult && typeof searchResult === 'object') {
      // Có thể là object đơn hoặc có property chứa array
      if (Array.isArray(searchResult.data)) {
        results = searchResult.data;
      } else if (searchResult.data && typeof searchResult.data === 'object') {
        results = [searchResult.data];
      } else if (searchResult.content) {
        results = [searchResult];
      } else if (searchResult.length !== undefined && typeof searchResult.length === 'number') {
        // Có thể là array-like object
        results = Array.from(searchResult);
      } else {
        // Thử parse như object đơn
        results = [searchResult];
      }
    }
  } catch (e) {
    console.error('Lỗi khi parse kết quả:', e.message);
    results = [];
  }

  // Không có kết quả
  if (!results || results.length === 0) {
    return [{
      json: {
        success: false,
        action: 'search',
        message: `Không tìm thấy thông tin liên quan đến: "${input.query || 'N/A'}"`,
        query: input.query || 'N/A',
        results: []
      }
    }];
  }

  // Format kết quả - với xử lý lỗi cho từng item
  const knowledge = [];

  for (const r of results) {
    try {
      if (r && (r.content || r.text)) {
        knowledge.push({
          content: r.content || r.text || '',
          category: r.category || 'general',
          similarity: r.similarity ? (typeof r.similarity === 'number' ? (r.similarity * 100).toFixed(1) + '%' : r.similarity) : 'N/A',
          id: r.id || null,
          metadata: r.metadata || null
        });
      }
    } catch (e) {
      console.error('Lỗi khi format item:', e.message);
      // Bỏ qua item lỗi, tiếp tục với item khác
    }
  }

  // Lọc bỏ kết quả rỗng
  const validKnowledge = knowledge.filter(k => k.content && k.content.trim().length > 0);

  if (validKnowledge.length === 0) {
    return [{
      json: {
        success: false,
        action: 'search',
        message: `Không tìm thấy thông tin liên quan đến: "${input.query || 'N/A'}"`,
        query: input.query || 'N/A',
        results: []
      }
    }];
  }

  // Tạo text tóm tắt
  const summaryText = validKnowledge.map((k, i) => 
    `${i + 1}. [${k.category}] ${k.content}${k.similarity !== 'N/A' ? ' (độ khớp: ' + k.similarity + ')' : ''}`
  ).join('\n');

  return [{
    json: {
      success: true,
      action: 'search',
      message: `Tìm thấy ${validKnowledge.length} thông tin liên quan:\n\n${summaryText}`,
      query: input.query || 'N/A',
      results: validKnowledge
    }
  }];

} catch (error) {
  // Xử lý mọi lỗi không mong đợi
  console.error('Lỗi không mong đợi trong Format Search Result:', error);

  let query = 'N/A';
  try {
    const input = $('Parse Input').first().json;
    query = input.query || 'N/A';
  } catch (e) {
    // Không thể lấy query
  }

  return [{
    json: {
      success: false,
      action: 'search',
      message: `Đã xảy ra lỗi khi xử lý kết quả tìm kiếm: ${error.message || 'Lỗi không xác định'}`,
      query: query,
      results: [],
      error: error.message || 'Unknown error'
    }
  }];
}

const response = $input.first().json;
