// Initialize the output arrays
const steps = [];
const tokens = [];
let totalTokens = 0; // Changed to track total tokens instead of cost

// Process each item in the input
for (const item of $input.all()) {
  let data = item.json;

  // Check if the data is an array itself (like your example JSON)
  if (Array.isArray(data)) {
    // Process each object in the array
    for (const obj of data) {
      // Extract steps information
      if (obj.intermediateSteps && Array.isArray(obj.intermediateSteps)) {
        for (const step of obj.intermediateSteps) {
          if (step.action) {
            steps.push({
              tool: step.action.tool,
              toolInput: step.action.toolInput,
              observation: step.observation
            });
          }
        }
      }

      // Extract token information
      if (obj.intermediateSteps && Array.isArray(obj.intermediateSteps)) {
        for (const step of obj.intermediateSteps) {
          if (step.action && step.action.messageLog && Array.isArray(step.action.messageLog)) {
            for (const message of step.action.messageLog) {
              if (message.kwargs && message.kwargs.response_metadata && message.kwargs.response_metadata.usage) {
                const usage = message.kwargs.response_metadata.usage;
                tokens.push({
                  prompt_tokens: usage.prompt_tokens,
                  completion_tokens: usage.completion_tokens,
                  total_tokens: usage.total_tokens,
                  model_name: message.kwargs.response_metadata.model_name
                });

                // Add the tokens to our running total
                if (typeof usage.total_tokens === 'number') {
                  totalTokens += usage.total_tokens;
                }
              }
            }
          }
        }
      }
    }
  } else {
    // If data is not an array, process it as a single object
    // Extract steps information
    if (data.intermediateSteps && Array.isArray(data.intermediateSteps)) {
      for (const step of data.intermediateSteps) {
        if (step.action) {
          steps.push({
            tool: step.action.tool,
            toolInput: step.action.toolInput,
            observation: step.observation
          });
        }
      }
    }

    // Extract token information
    if (data.intermediateSteps && Array.isArray(data.intermediateSteps)) {
      for (const step of data.intermediateSteps) {
        if (step.action && step.action.messageLog && Array.isArray(step.action.messageLog)) {
          for (const message of step.action.messageLog) {
            if (message.kwargs && message.kwargs.response_metadata && message.kwargs.response_metadata.usage) {
              const usage = message.kwargs.response_metadata.usage;
              tokens.push({
                prompt_tokens: usage.prompt_tokens,
                completion_tokens: usage.completion_tokens,
                total_tokens: usage.total_tokens,
                model_name: message.kwargs.response_metadata.model_name
              });

              // Add the tokens to our running total
              if (typeof usage.total_tokens === 'number') {
                totalTokens += usage.total_tokens;
              }
            }
          }
        }
      }
    }
  }
}

// Return the processed data with total_tokens included
